function initYouTubeThumbnail() {
	jQuery('.generic-thumbnail').each(function(index) {
		var link = jQuery(this);
		var url = link.attr('href');
		var image = jQuery('<img />');
		var srcImage = getSrcYoutubeThumbnail(getIdVideo(url));

		function getSrcYoutubeThumbnail(id) {
			return 'http://img.youtube.com/vi/' + id + '/0.jpg';
		}

		function getIdVideo(str) {
			var newStr = str.split('http://www.youtube.com/embed/')[1];
			var id = newStr.split(/(\?)/)[0];

			return id;
		}

		function showImage() {
			image.attr('src', srcImage);
			link.append(image);
		}

		showImage();
	});
}

